import React from "react";
import Book from "./Book";

export default function ViewBook(props) {
  return (
    <div className="view-container">
      <Book
        src="https://m.media-amazon.com/images/I/81rq4w91g0L._AC_UF1000,1000_QL80_.jpg"
        title="Mahabaratam"
        author="Vyasa"
        date="400 BCE"
        desc="The Mahābhārata is one of the two major Sanskrit epics of ancient India in Hinduism, the other being the Rāmāyaṇa. It narrates the struggle between two groups of cousins in the Kurukshetra War and the fates of the Kaurava and the Pāṇḍava princes and their successors."
      />
      {props.booksArray?.map(function (book) {
        return (
          <Book
            src={book.src}
            title={book.title}
            author={book.author}
            date={book.date}
            desc={book.desc}
          />
        );
      })}
    </div>
  );
}
